import { Col, Container, ListGroup, Row } from "react-bootstrap";
import { BsFillXOctagonFill } from "react-icons/bs";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";

const FavoriteList = () => {
  const favoriteJobs = useSelector((state) => state.favoriteJobs.content);

  return (
    <Container>
      <Row>
        <Col xs={10} className='mx-auto my-3'>
          <h1 className='display-1'>Favorite jobs list</h1>
          <Link to='/' className='nav-link'>
            <h5 className='ms-auto text-primary'>Go back</h5>
          </Link>
        </Col>

        <Col xs={10} className='mx-auto mb-5'>
          <ListGroup as='ul'>
            {favoriteJobs.map((jobData) => (
              <ListGroup.Item as='li' className='d-flex'>
                {jobData.company_name}
                <BsFillXOctagonFill
                  className='ms-auto'
                  onClick={() => {
                    dispatch({ type: "REMOVE_FROM_FAVORITE", payload: data._id });
                  }}
                />
              </ListGroup.Item>
            ))}
          </ListGroup>
        </Col>
      </Row>
    </Container>
  );
};

export default FavoriteList;
