const mainState = {
  query: {
    content: " ",
  },

  jobs: {
    content: [],
  },
};

const mainReducer = (state = mainState, action) => {
  switch (action.type) {
    case "ADD_QUERY":
      return {
        ...state,
        query: {
          content: action.payload,
        },
      };

    default:
      state;
  }
};

export default mainReducer;
